INSERT INTO `dp_breakingnews` (`breakingnews_id`, `breakingnews_title`, `breakingnews_title_ar`, `breakingnews_news`, `breakingnews_news_ar`, `breakingnews_link`, `breakingnews_image`, `breakingnews_date`, `breakingnews_status`) VALUES
(1, 'DUSUP Awards Excelerate Energy FSRU Charter in Dubai', '', 'September 22nd, 2014 -Dubai Supply Authority (DUSUP) has enteredinto a 10-year charter agreement with Excelerate Energy under which Excelerate is to provide andoperate a floating storage and regasification unit (FSRU) that will be located at DUSUP''s existing JebelAli LNG import terminal.', '', 'http://dusup.ae/PDF/Press_Release.pdf', 'imgNews1.jpg', 1400356800, 0),
(2, 'DUSUP Completes first Gas-Up Cool Down operation in Dubai', '', 'October 13th, 2014 - DUSUP has completed its first Gas Up and CoolDown operation in Dubai. The operation was performed on the LNG regasification vessel EXPLORER(which is jointly owned by Excelerate Energy and Exmar, managed by Exmar Ship Management andchartered by Excelerate Energy) in Dubai waters using the DUSUP Chartered Multi-gas Vessel NORGASUNIKUM. ', '', 'http://dusup.ae/PDF/141013_GUCD_DUSUP_PR.pdf', 'imgNews2.jpg', 1400356800, 0),
(3, 'The Q-Max Vessel LNG delivery to Dubai from Qatar', '', 'October 28th 2014 - For the first time Qatar gas Operating Company Limited (Qatargas) has delivered LNG on-board a Q-Max Vessel to the Dubai Supply Authority (DUSUP) LNG Terminal located at the Jebel Ali Port.', '', 'http://dusup.ae/PDF/pr3.pdf', 'qmax.jpg', 1400356800, 0),
(7, 'New Gas Pipeline to Hassyan', '', 'March 2015: DUSUP have completed the installation of a 38 kilometers long 48â€ natural gas pipeline to the proposed Hassyan Power station facility . The new pipeline significantly increases gas distribution capability and adds approximately 100 MMscf of line pack to the Dubai gas supply and distribution network.', '', '', 'latestnews_1468139136.jpg', 1468137885, 1),
(8, 'FSRU Explorer upgrade completed in Dubai Dry Docks', '', 'January 2016: The FSRU Explorer has completed 3 months of upgrades in Dubai Dry Dock adding advanced features to improve the services on the vessel including new high-pressure (HP) vaporizers and pumps (increase the maximum regasification send-out capacity), a dual-fuel diesel generator power module added to provide more fuel-efficient power generation and the addition of the worldâ€™s first LNG bunker port installed in an FSRU which enables small-scale LNG off-takers to take LNG directly.', '', '', 'latestnews_1468146385.png', 1468140637, 1),
(9, 'New milestone for Dubai FSRU terminal.', '', 'February 2016 : The newly upgraded FSRU Explorer has completed its send-out performance tests and reached a maximum send-out capacity of 1.0 Bcf/d â€“ an unprecedented milestone for an FSRU. The increase in regasification capacity will allow DUSUP to import more LNG cargoes during the year providing greater flexibility to adapt to market conditions.', '', '', 'latestnews_1468146758.jpg', 1468140999, 1),
(10, 'DUSUP completes Margham expansion.', '', 'June 2016: The Margham gas storage facility has undergone a capacity expansion which increases both export capacity and reliability.  The expansion includes a larger well manifold , debottlenecked well flow lines, high capacity gas inlet section with slug catcher and gas coolers and a new high capacity cold plant featuring high efficiency heat exchangers.   The new equipment has been integrated into the existing plant and will provide increased operational flexibility for gas storage and Export.', '', '', 'latestnews_1468141343.jpg', 1468141342, 1),
(11, 'Commercial Gas-up Operations.', '', 'September 2016:  DUSUP has completed the first two commercial gas-up operations at its LNG import terminal in Jebel Ali.\n\nThis was successfully done using the FSRU Explorer in an operation which gassed-up all five tanks of a 173,000 cu m of each LNG Carrier in a 15-20 hour period.\n\nDUSUPâ€™s ability to utilise the FSRU for these operations will allow LNG vessels departing regional dry docks avoid having to travel significant distances to perform this required step in the LNG cargo cycle, making Dubai a full-service LNG hub.\n\nThis achievement not only demonstrates the enhanced capabilities of the newly upgraded Explorer, but it also illustrates the versatility of the DUSUP terminals in its ability to provide more than just regasification services. Gas-up is one of the mandatory operations an LNG carrier must perform to make the cargo tanks ready to receive LNG cargoes after leaving the dry dock.\n\nAs part of the completion of Explorer''s upgrades in early 2016 the vessel can now provide gas-up, cool-down, and loading services to LNG carriers. The FSRU was also upgraded to include an LNG bunker port to service small-scale LNG offtakers.', '', '', 'latestnews_1475133947.jpg', 1475132822, 1);
