INSERT INTO `dp_history` (`history_id`, `history_year`, `history_image`, `history_content`, `history_content_ar`, `history_status`) VALUES
(1, 1980, '1980.jpg', 'A concession covering an area known as Margham Field was granted to ARCO Inc, a company established in the State of Delaware, USA for the production of light condensate oil.', '', 1),
(2, 1984, '1984.jpg', 'First condensate oil production from Margham Field.', '', 1),
(3, 1984, '1984.jpg', 'First export of condensate oil from Jebel Ali port via vessel Palm Starr Cherry, destination Japan.', '', 0),
(4, 1986, '1986.jpg', 'Agreement reached to import natural gas via pipeline from Sharjah, UAE.', '', 1),
(5, 1992, '1992.jpg', 'DUSUP was formed under Decree Number 2 - 1992 to produce, receive, collect, transport, refine, store, buy, sell, barter and distribute natural gas, undertake projects required for the transportation of natural gas from suppliers to industrial customers in the Emirate of Dubai.', '', 1),
(6, 1993, '1993.jpg', 'Gas Control Station (GCS) built at Jebel Ali Industrial Area -1.', '', 1),
(7, 1998, '1998.jpg', 'ISO 14001 acreditation.', '', 0),
(8, 1999, '1999.jpg', 'ARCO Inc bought by BP plc. Margham Field concession transfers to BP.', '', 1),
(9, 2000, '2000.jpg', 'Margham Dubai Establishment was formed under Decree Number 23 of the year 2000. BP agrees to sell the Margham Field to the Dubai Government , and its concession is cancelled.', '', 1),
(10, 2001, '2001.jpg', 'First gas from ADNOC.', '', 1),
(11, 2004, '2004.jpg', 'Agreement between DUSUP and MDE for operation and maintenance of DUSUP pipelines and related facilities.', '', 0),
(12, 2006, '2006.jpg', 'Approval given to implement the Dubai LNG project. Royal Dutch Shell appointed as technical and safety advisors to the project.', '', 1),
(13, 2007, '2007.jpg', 'GCS was expanded to handle a daily capacity of 3BCF of Gas.<br><br>First gas delivered to Dubai from Qatar by Dolphin Energy.<br><br>Margham Gas Field implemented a horizontal drilling programme using coil tubing underbalanced drilling technology to increase the deliverability of their Gas wells.', '', 0),
(14, 2007, '2007.jpg', 'First gas delivered to Dubai from Qatar by Dolphin Energy', '', 1),
(15, 2007, '2007.jpg', 'Margham Gas Field implemented a horizontal drilling programme using coil tubing underbalanced drilling technology to increase the deliverability of their Gas wells.', '', 0),
(16, 2008, '2008.jpg', 'Margham Field becomes a gas storage facility, injecting imported dry gas for the first time for<br>re-export during the summer peak demand period.', '', 1),
(17, 2009, '2009.jpg', 'Decree Number 19 of the same year says that DUSUP takes over the ownership of Margham Dubai Establishment, with all rights, commitments and employees transferred to DUSUP.', '', 1),
(18, 2010, '2010.jpg', 'LNG Operations began in Q4. Floating Storage Regasification Unit vessel Golar Freeze chartered to regasify LNG for delivery into the pipeline network', '', 1),
(19, 2011, '2011.jpg', 'Compressors reconfigured for low pressure services to save fuel.', '', 1),
(20, 2012, '2012.jpg', 'Dubai Integrated Gas Strategy 2030 approved - sets future direction for DUSUP.', '', 1),
(21, 2013, '2013.jpg', 'Margham fuel reduced 63% from previous years following compressor reconfiguration and introduction of new operating modes.', '', 1),
(22, 2014, '2014.jpg', '20" gas line to DPE shore connection point completed.  Re-routed Margham condensate flow directly into ENOC refinery.  Margham free-flow capacity increased 35%.  Commitment to replace current LNG FSRU, thereby assuring LNG''s long term place in Dubai''s fuel mix.  1st LNG Gas Up Cool Down completed. ', '', 1),
(23, 2015, '2015.jpg', 'LNG Terminal import capacity increased by employing a larger Floating Storage Regasification Unit vessel Explorer.', '', 1);
